class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ans = 0;
        int n = nums.size();
        vector<int> dif(n+1), dif2(n+1);
        for(int i=0; i<n; i++) {
            dif[i] = target[i] - nums[i];
            if(i == 0) dif2[i] = dif[i];
            else dif2[i] = dif[i] - dif[i-1];
        }
        dif2[n] = 0 - dif[n-1];
        for(int i=0; i<=n; i++) {
            if(dif2[i] > 0) ans += dif2[i];
        }
        return ans;
    }
};
