class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        n=len(nums)
        diff=[target[i]-nums[i] for i in range(n)]
        pre=diff[0]
        res=0
        for i in range(1,n):
            if diff[i]!=pre:
                if diff[i]*pre<=0:
                    res+=abs(pre)
                else:
                    if pre>0 and diff[i]<pre:
                        res+=pre-diff[i]
                    elif pre<0 and diff[i]>pre:
                        res+=diff[i]-pre
                pre=diff[i]
        res+=abs(pre)
        return res
