class Solution:
    def minimumOperations(self, nums, target):
        n = len(nums)
        diff = [0] * (n + 5)

        for i in range(n): diff[i] = target[i]-nums[i]

        for i in range(n, 0, -1): diff[i] -= diff[i-1]

        ans = 0
        for i in range(n+1):
            if diff[i] > 0: ans += diff[i]

        return ans
