class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        diff = [target[i] - nums[i] for i in range(len(nums))]
        total_ops = 0
        current_value = 0

        for i in range(len(diff)):
            if diff[i] != 0:
                if i == 0 or (diff[i-1] > 0 and diff[i] > 0) or (diff[i-1] < 0 and diff[i] < 0):
                    change = abs(diff[i]) - abs(current_value)
                    if change > 0:
                        total_ops += change
                    current_value = diff[i]
                else:
                    total_ops += abs(diff[i])
                    current_value = diff[i]
            else:
                current_value = 0

        return total_ops
