class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n=nums.size();
        for(int i=0; i<n; i++){
            target[i]-=nums[i];
        }
        long long int c=abs(target[0]); 

        for(int i=1; i<n; i++){
            if(target[i]<0 && target[i-1]<0){
                if(abs(target[i]) > abs(target[i-1])) c+=abs(target[i-1]-target[i]);
            }
            else if(target[i]>0 && target[i-1]>0){
                if(target[i]>target[i-1]) c+=abs(target[i-1]-target[i]);
            }
            else{
                c+=abs(target[i]);
            }
        }
        return c;
    }
};
