class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        diff=[t-num for num,t in zip(nums,target)]
        n=len(nums)
        ans=0
        # print(diff)
        i=0
        while i<n:
            ans+=abs(diff[i])
            while i+1<n and diff[i+1]*diff[i]>0:
                i+=1
                if abs(diff[i])>abs(diff[i-1]):
                    ans+=abs(diff[i])-abs(diff[i-1])
            # print(i,ans)
            i+=1
        return ans
