class Solution {
public:
#define ll long long int
    long long minimumOperations(vector<int>& v, vector<int>& t) {
        ll n=v.size();
        vector<ll>d;
        for(int i=0;i<n;i++)d.push_back(t[i]-v[i]);
        ll c=abs(d[0]);
        for(int i=0;i<n-1;i++){
            if(d[i]==d[i+1])continue;
            if(d[i]>=0 and d[i+1]<0){c+=abs(d[i+1]);continue; }
            if(d[i]<0 and d[i+1]>=0){c+=abs(d[i+1]);continue; }
            ll a=abs(d[i]),b=abs(d[i+1]);
            if(a<b)c+=b-a;
        }
        return c;
    }
};
