class Solution {
        public long minimumOperations(int[] nums, int[] target) {
        int n = nums.length;
        int[] diff = new int[n];
        int last = 0;
        for (int i = 0; i < n; i++) {
            target[i] = target[i] - nums[i];
            diff[i] = target[i] - last;
            last = target[i];
        }
        long ans =0;
        long ans2 = 0;
        for (int i = 0; i < n; i++) {
            if (diff[i] < 0) {
                ans += -1l * diff[i];
            } else {
                ans2 += diff[i];
            }
        }
        return Math.max(ans, ans2);
    }


}
