class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        n = len(nums)
        diffs = [target[i] - nums[i] for i in range(n)]

        res = abs(diffs[0])
        for i in range(1, n):
            curr = diffs[i]
            last = diffs[i - 1]
            curr_pos = True if curr >= 0 else False
            last_pos = True if last >= 0 else False
            if curr_pos and last_pos:
                if curr > last:
                    res += curr - last
            if not curr_pos and not last_pos:
                if curr < last:
                    res += last - curr
            if curr_pos and not last_pos:
                res += curr
            if not curr_pos and last_pos:
                res += abs(curr)
        return res
