class Solution {
public:
    typedef long long ll;
    long long minimumOperations(vector<int>& nums, vector<int>& tar) {


        int n = nums.size();
        ll last = 0;
        ll ans = last;

        for(int i = 0 ;i <n;i++){
            ll x = tar[i] - nums[i];
            if(x >= 0){
                if(last >= x){
                    // ans += abs( last - x );
                    last = x;
                }else{
                    if(last < 0) last = 0;

                    ans += abs(last - x);
                    last = x;
                }

            }else{

                if(last <= x){
                    last = x;
                }else{

                    if(last > 0) last = 0;

                    ans += abs(last - x);
                    last = x;

                }

            }

            // cout<<ans<<" ";

        }
        // cout<<endl;
        return ans;


    }
};
