#include <bits/stdc++.h>
using namespace std;
typedef long long ll;
typedef double db;
typedef pair<int, int> pii;

class Solution {
public:
    ll solve(vector<ll> vec){
        ll res = 0, ope = 0;
        int n = vec.size();
        int o = 1;
        if(vec[0] < 0 )
            o = -1;
        for (int i = 0; i < n;i++){
            vec[i] *= o;
            if(vec[i] <= ope)
            {
                ope = vec[i];
            } else if(vec[i] > ope){
                res += vec[i] - ope;
                ope = vec[i];
            }
        }
        return res;
    }
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector<long long> vec(n);

        for (int i = 0; i < n;i++){
            vec[i] = target[i] - nums[i];
        }
        ll res = 0, ope = 0;
        vector<ll> temp;
        temp.push_back(vec[0]);
        for (int i = 1; i < n;i++){
            if(vec[i] * temp[temp.size() - 1] > 0){
                temp.push_back(vec[i]);
            } else {
                res += solve(temp);
                temp.clear();
                temp.push_back(vec[i]);
            }
        }
        return res + solve(temp);
    }
};
