typedef long long int ll;

class Solution {
public:
    ll minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();


        ll prev = target[0] - nums[0], ans = abs(prev);

        for(int i=1; i<n; i++) {
            ll curr = target[i] - nums[i];

            // cout<<i<<" "<<prev<<" "<<curr<<" "<<ans<<endl;

            if((curr >= 0 && prev <= 0) || (curr <= 0 && prev >= 0)) {
                // change of sign, all previous ops get reset
                ans += abs(curr);
                prev = curr;
                // cout<<"Hereeeee"<<endl;
            } else {

                if(curr > 0) {
                    // no change of sign
                    if(curr > prev) {
                        // require new ops
                        ans += abs(curr - prev);
                        prev = curr;
                    } else {
                        // finish few previous ops, starting nothing new
                        prev = curr;
                    }
                } else {
                    if(curr < prev) {
                        // requires new ops
                        ans += abs(curr - prev);
                        prev = curr;
                    } else {
                        // finish few previous ops, starting nothing new
                        prev = curr;
                    }
                }
            }
        }

        return ans;
    }
};
