#include <bits/stdc++.h>
#define sz size()
#define bk back()
#define fi first
#define se second

using namespace std;
typedef long long ll;
typedef pair<int, int> pii;

// -1 2 -2
class Solution {
  public:
    long long minimumOperations(vector<int> &nums, vector<int> &target) {
        int n = nums.sz;

        vector<ll> v(n);
        for (int i = 0; i < n; i++)
            v[i] = nums[i] - target[i];

        ll ret = 0;
        ll k = 0;

        for (int i = 0; i < n; i++) {
            if (v[i] > 0 && k < 0) {
                if (v[i] + k < 0)
                    k = -v[i];

                v[i] += k;
            } else if (v[i] < 0 && k > 0) {
                if (v[i] + k > 0)
                    k = -v[i];

                v[i] += k;
            } else
                k = 0;

            ret += abs(v[i]);
            k -= v[i];
        }

        return ret;
    }
};
