class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ans=0;
        int n = nums.size();
        vector<int>dif(n+2,0);

        for(int i = 0; i < n; i++){
            dif[i+1] = target[i] - nums[i];
            cout<<dif[i+1]<<" ";
        }
        int i = 0;
        while(i < n+2){
            if(dif[i]>0){
                while(i<n+2 && dif[i]>0){
                    if(dif[i] > max(0, dif[i-1])){
                        ans+=dif[i]-max(0, dif[i-1]);
                    }
                    i++;
                }
            }else if(dif[i]<0){
                while(i<n+2 && dif[i]<0){
                    if(dif[i] < min(0, dif[i-1])){
                        ans-=dif[i]-min(0, dif[i-1]);
                    }
                    i++;
                }
            }else{
                i++;
            }
        }

        return ans;
    }
};
