class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ans = 0;
        vector<int> res_v;
        for(int i = 0; i < nums.size(); i++) {
            if(res_v.size() > 0 && res_v.back() == (target[i] - nums[i])) continue;
            else res_v.push_back(target[i] - nums[i]);
        }
        ans += abs(res_v[0]);
        for(int i = 1; i < res_v.size(); i++){
            if(res_v[i] >= 0) {
                if(res_v[i-1] >= 0 && res_v[i] > res_v[i-1]) ans += (res_v[i] - res_v[i-1]);
                else if(res_v[i-1] < 0) ans += res_v[i];
            } else {
                if(res_v[i-1] < 0 && res_v[i] < res_v[i-1]) ans += (res_v[i-1] - res_v[i]);
                else if(res_v[i-1] >= 0) ans += abs(res_v[i]);
            }
        }
        return ans;
    }
};
