class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        vector<long long> arr;
        for(int i=0;i<nums.size();i++){
            arr.push_back(nums[i] - target[i]);
        }
        long long ans = 0;
        long long a = 0;
        for(int i=0;i<arr.size();i++){
            if(arr[i] > 0){
                int j = i+1;
                a = arr[i];
                ans += a;
                while(j < arr.size() && arr[j] > 0){
                    if(a < arr[j]){
                        ans += arr[j] - a;
                    }
                    a = arr[j];
                    j++;
                }
                i = j-1;
                a = 0;
            }
            else{
                int j = i+1;
                a = -(arr[i]);
                ans += a;
                while(j < arr.size() && arr[j] < 0){
                    if(a < -(arr[j])){
                        ans += -(arr[j]) - a;
                    }
                    a = -(arr[j]);
                    j++;
                }
                i = j-1;
                a = 0;
            }
        }
        return ans;

    }
};
