class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        vector<int>newt;
        int n=nums.size();
        for (int i=0;i<n;i++){
            newt.push_back(target[i]-nums[i]);
        }
        vector<vector<int>>v;
        vector<int>curr;
        curr.push_back(abs(newt[0]));
        for (int i=1;i<n;i++){
            if (newt[i]>=0 && newt[i-1]<0){
                v.push_back(curr);
                curr.clear();
            } else if (newt[i]<0 && newt[i-1]>=0){
                v.push_back(curr);
                curr.clear();
            }
            curr.push_back(abs(newt[i]));

        }
        if (curr.size()>0) v.push_back(curr);
        long long total=0;
        for (auto x:v){
            vector<int>&curr=x;
            long long pre=0; long long res=0;
            for (auto a:curr){
                res+=max((long long)a-pre,(long long)0);
                pre=(long long)a;
            }
            total+=res;
        }
        return total;
    }
};
