int fop(int prev, int diff) {
    if (!diff)
        return 0;
    if (diff < 0 && prev >= 0)
        return abs(diff);
    if (diff > 0 && prev <= 0)
        return diff;
    return max(abs(diff) - abs(prev), 0);
}

using ll = long long;

class Solution {
  public:
    long long minimumOperations(vector<int> &nums, vector<int> &target) {
        int n = nums.size();
        ll ops = 0, prev = 0;
        for (int i = 0; i < n; ++i) {
            int diff = target[i] - nums[i];
            ops += fop(prev, diff);
            prev = diff;
        }
        return ops;
    }
};
