class Solution {
public:
    long long f(vector<int>&v){
        // for(auto&it:v)cout<<it<<' ';cout<<endl;
        int lm = -1;
        long long ct = 0;
        int n =v.size();
        for(int i=0;i<n;i++){
            if(v[i]<=lm){
                if(lm!=-1) lm = min(lm,v[i]);
                continue;
            }
            if(lm==-1){
                ct+=v[i];
                lm = v[i];
                continue;
            }
            ct+=(v[i]-lm); 
            lm = v[i];
        }
        // cout<<ct<<endl;
        return ct;
    }
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector<int>diff;
        for(int i=0;i<n;i++){
            diff.push_back(nums[i]-target[i]);
        } 
        // for(auto&it:diff)cout<<it<<' ';cout<<endl;
        long long ans=  0;
        int ct = 0;
        bool pos = 1;
        vector<int>tr;
        bool ln = 1;
        for(int i=0;i<n;i++){
            if(diff[i]>=0){
                if(!ln){
                    ans+=f(tr);
                    tr.clear();
                }
                tr.push_back(abs(diff[i]));
                ln = 1;
            }
            else{
                if(ln){
                    ans+=f(tr);
                    tr.clear();
                }
                ln = 0;
                tr.push_back(abs(diff[i]));
            }
        }
        if(tr.size()) ans+=f(tr);
        return ans;
    }
};
