class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        l = [y-x for x,y in zip(nums,target)]
        res = [l[0]]
        for x,y in pairwise(l):
            res.append(y - x)
        res.append(-sum(res))
        return sum(x for x in res if x > 0)
