class Solution {
public:
    long long minimumOperations(vector<int>& a, vector<int>& v) 
    {

     int i = 0 , n = a.size();
     long long ans = 0;

     while( i < n )
     {
        int j = i ;

        if( v[i] == a[i] )
        {
            i++;
            continue;
        }

        long long diff1 = v[i] - a[i];
        while( j < n ) 
        {
        long long diff2 = v[j] - a[j];
        if( diff1 > 0 && diff2 <= 0 )
        break;
        if( diff1 < 0 && diff2 >= 0 )
        break;     
        j++;
        }

        long long k = i , prev = 0 ;

        while( k < j )
        {
            long long d = abs(v[k]-a[k]);
            if( d >= prev )
            {
                ans += (d-prev);
                prev = d;
            }
            else
                prev = d;
            k++;
        }

        i = j;
     }    

     return ans;
    }
};
