#include <iostream>
#include <bits/stdc++.h>
using namespace std;
class Solution
{
public:
    long long minimumOperations(vector<int> &nums, vector<int> &target)
    {
        long long opr = 0;
        long long prev = 0;
        for (int i = 0; i < nums.size(); i++)
        {
            if (nums[i] < target[i])
            {
                if (prev < 0)
                    prev = 0;
                long long incr = target[i] - nums[i];
                if (incr > prev)
                {
                    opr += incr - prev;
                    prev = incr;
                }
                else
                {
                    prev = incr;
                }
            }
            else
            {
                if (prev > 0)
                    prev = 0;
                long long decr = abs(target[i] - nums[i]);
                if (decr > abs(prev))
                {
                    opr += abs(decr - abs(prev));
                    prev = -decr;
                }
                else
                {
                    prev = -decr;
                }
            }
        }
        return opr;
    }
};
