class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        n = len(nums)
        diff = [0] * (n + 1)
        for i, (x, y) in enumerate(zip(nums, target)):
            diff[i + 1] = y - x
        a = b = 0
        for x, y in zip(diff[:-1], diff[1:]):
            c = y - x
            if c < 0:
                a -= c
            else: 
                b += c 
        return max(a, b)
