class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        long long ans1 = 0, ans2 = 0;
        vector<int> a(n), b(n);
        for(int i = 0; i < n; i++) {
            if(i == 0) {
                a[i] = nums[i];
                b[i] = target[i];
            } else {
                a[i] = nums[i] - nums[i - 1];
                b[i] = target[i] - target[i - 1];
            }
            if(b[i] - a[i] > 0) ans1 += 1ll * (b[i] - a[i]);
            else ans2 += 1ll * (a[i] - b[i]);
        }
        return max(ans1, ans2);
    }
};
