class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ans =0 ;
        vector<long long> incre;
        long long prevDiff = 0;
        long long i = 0;
        while(i<nums.size())
        {
            long long m = target[i]-nums[i];
            if(prevDiff>=0)
            {
                if(m>=0 && m>=prevDiff)
                {
                    ans = ans+(m-prevDiff);
                }
                if(m<0)
                {
                    ans = ans+abs(m);
                }
            }
            else
            {
                if(m<=0 && m<=prevDiff)
                {
                    ans = ans+abs(m-prevDiff);
                }
                if(m>0)
                {
                    ans = ans+abs(m);
                }
            }

            prevDiff = m;
            cout<<prevDiff<<" "<<ans<<endl;
            i++;
        }
        return ans;
    }
};
