class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:

        def sign(x):

            if x>0:
                return 1
            if x<0:
                return -1

            return 0

        diff=[0]*len(target)

        diff[0]=target[0]-nums[0]
        res=abs(diff[0])

        for i in range(1,len(target)):
            diff[i]=target[i]-nums[i]

            if sign(diff[i])==sign(diff[i-1]):

                if sign(diff[i])==1:
                    if diff[i]>diff[i-1]:
                        res+=abs(diff[i]-diff[i-1])

                elif sign(diff[i])==-1:
                    if diff[i]<diff[i-1]:
                        res+=abs(diff[i]-diff[i-1])

            else:
                res+=abs(diff[i])

        return res
