class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        vector<int> a;
    long long ans = 0;
    for (int i = 0; i < nums.size(); i++) {
        a.push_back(target[i] - nums[i]);
    }
    int last = 0;
    for (int i = 0; i < a.size(); i++) {
        if (a[i] == 0) {
            last = 0;
            continue;
        }
        long long temp0 = 0, tempLast = 0;
        temp0 = abs(a[i]);

        if (a[i] > 0) {
            if (last < a[i]) {
                tempLast = abs(a[i] - last);
            }
        } else {
            if (last > a[i]) {
                tempLast = abs(a[i] - last);
            }
        }
        ans += min(tempLast, temp0);
        // dbg(temp0, tempLast);
        last = a[i];
    }
    return ans;
    }
};
