class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& arr) 
    {
        int n=nums.size();
        vector<vector<long long int>> dp(n,vector<long long int>(2,0));
        dp[n-1][0]=abs(arr[n-1]-nums[n-1]);
        dp[n-1][1]=arr[n-1]-nums[n-1];

        for(int i{n-2};i>=0;i--)
        {
            long long int a=arr[i]-nums[i],b=dp[i+1][1];
            long long int x=a*b;

            if(x>0)
            {
                 long long int a1=abs(a),b1=abs(b);

                 if(b1<a1)
                 {
                    // cout<<" aka ";
                    dp[i][0]=dp[i+1][0]+(a1-b1);
                 }
                 else
                 {
                    // cout<<" ana ";
                    dp[i][0]=dp[i+1][0];
                 }

                 dp[i][1]=a;
            }
            else
            {
                dp[i][0]=dp[i+1][0]+(abs(a));
                dp[i][1]=a;
            }

            // cout<<dp[i][0]<<" "<<i<<endl;
        }

        return dp[0][0];
    }
};
