class Solution {
    public long minimumOperations(int[] nums, int[] target) {
        int n = nums.length;
        int[] dif = new int[n];
        for (int i = 0; i < n; i++) {
            dif[i] = target[i] - nums[i];
        }
        long ans = 0;
        int pre = 0;
        for (int i = 0; i < n; i++) {
            if (dif[i] == pre) {
                continue;
            } else {
                if (pre > 0 != dif[i] > 0) {
                    ans += Math.abs(pre);
                    pre = dif[i];
                } else {
                    if (Math.abs(dif[i]) >= Math.abs(pre)) {
                        pre = dif[i];
                    } else {
                        ans += Math.abs(pre) - Math.abs(dif[i]);
                        pre = dif[i];
                    }
                }
            }
        }
        ans += Math.abs(pre);
        return ans;
    }
}
