class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        using ll = long long;
        ll re = abs(nums[0]-target[0]),cnt = abs(nums[0]-target[0]),p = nums[0] < target[0];
        // cout << re << endl;
        for(int i=1,n=nums.size();i<n;i++){
            int t = nums[i] - target[i],np = nums[i] < target[i];
            if(p != np){
                cnt=abs(t);
                re+=abs(t);
                p = np;
            } else {
                // cout << t << ' ' << cnt << endl;
                if(abs(t)>cnt) re+=abs(t)-cnt;
                cnt = abs(t);
            }
            // cout << re << endl;
        }
        return re;
    }
};
