class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ans = 0, track_last = 0, inc = 1;
        for(int i=0;i<nums.size();i++){
            if(nums[i] > target[i]){ // dec
                if(inc != 1){
                    track_last = 0;
                    inc = 1;
                }
                int curr_diff = nums[i] - target[i];
                if(track_last<curr_diff){
                    ans += (curr_diff-track_last);
                }
                track_last = curr_diff;
            } else if (nums[i]<target[i]){ // inc
                if(inc == 1){
                    track_last = 0;
                    inc = 0;
                }
                int curr_diff = target[i] - nums[i];
                if(track_last<curr_diff){
                    ans += (curr_diff-track_last);
                }
                track_last = curr_diff;
            } else {
                track_last = 0;
                inc=1;
            }
        }
        return ans;
    }
};
