class Solution {
public:
    long long minimumOperations(vector<int>& v, vector<int>& t) 
    {
        vector<int> target;
        int n = v.size();
        for(int i=0;i<n;i++) target.push_back(t[i] - v[i]);
        long long ans = 0;
        int i = 0;
        while(i<n)
        {
            int j = i;
            vector<int> cur = { target[i] };
            if( target[i] > 0 )
            {
                while(j+1<n && target[j+1] >= 0) {
                    cur.push_back( target[j+1] );
                    j++;
                }

            }else 
            {
                while(j+1<n && target[j+1] < 0) {
                    cur.push_back( target[j+1] );
                    j++;
                }
                for(int i=0;i<cur.size();i++) cur[i] *= -1;
            }


            int res = cur[0];
            for(int i=1;i<cur.size();i++)
            {
                res += max(0, cur[i] - cur[i-1]);
            }
            ans += res;
            i = j+1;
        }

        return ans;
    }
};
