class Solution {
    public long minimumOperations(int[] nums, int[] target) {
        int n = nums.length;
        for (int i = 0; i < n; i++) {
            nums[i] -= target[i];
        }

        long res = Math.abs(nums[0]);
        for (int i = 1; i < n; i++) {
            if (nums[i] >= 0 && nums[i - 1] >= 0) {
                if (nums[i] >= nums[i - 1]) {
                    res += nums[i] - nums[i - 1];
                } 
            } else if (nums[i] <= 0 && nums[i - 1] <= 0) {
                if (nums[i] <= nums[i - 1]) {
                    res += nums[i - 1] - nums[i];
                } 
            } else {
                res += Math.abs(nums[i]);
            }
        }
        return res;
    }
}
