class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        n = len(nums)
        for i in range(n):
            nums[i] = nums[i] - target[i]
        arr = [nums[0]]
        for i, j in pairwise(nums):
            arr.append(j - i)
        x, y = 0, 0
        for v in arr:
            if v < 0:
                x -= v
            else:
                y += v
        return max(x, y)
