import java.util.Arrays;
class Solution {
    public long minimumOperations(int[] nums, int[] target) {
        long ans = 0;
        int preDiff = 0, n = nums.length;
        for (int i=0; i<n; i++){

            int diff = target[i]-nums[i];
            int cur = Math.abs(diff);
            if (Integer.signum(preDiff)==Integer.signum(diff))
                cur = Math.min(cur, Math.max(0, Math.abs(diff) - Math.abs(preDiff)));
            diff = diff - preDiff;
            cur = Math.min(Math.abs(diff), cur);

            ans += cur;
            preDiff =  target[i]-nums[i];
        }
        return ans;
    }

    static void tr(Object... o) {
        System.out.println(Arrays.deepToString(o));
    }

}
