class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:

        def getTot(arr):
            arr = [abs(val) for val in arr]
            tot = 0
            for i in range(1, len(arr)):
                if arr[i] < arr[i-1]: tot += arr[i-1]-arr[i]

            return tot + arr[-1]

        diffs = [a-b for a, b in zip(nums, target)]

        tot = 0
        group = [diffs[0]]
        for i in range(1, len(diffs)):
            a, b = diffs[i-1], diffs[i]
            pa, pb = a < 0, b < 0
            if pa != pb: 
                tot += getTot(group)
                group = [b]
            else: group.append(b)
        tot += getTot(group)

        return tot
