class Solution {
public:
    long long minimumOperations(vector<int>& S, vector<int>& T) {
        int n = S.size();
        for (int i = 0; i < n; ++i) S[i] = T[i] - S[i];
        auto res = 0LL;

        auto p = 0LL;
        for (auto s : S) {
            if (s >= 0 && p >= 0)
                res += max(s - p, 0LL);
            else if (s < 0 && p < 0)
                res += max(abs(s) - abs(p), 0LL);
            else
                res += abs(s);
            p = s;
        }
        return res;
    }
};
