using i64 = int64_t;
class Solution {
 public:
  long long minimumOperations(vector<int>& nums, vector<int>& target) {
    i64 res = 0, n = ssize(target);
    vector<i64> x(n + 2);
    for (int i = 0; i < n; i += 1) x[i + 1] = target[i] - nums[i];
    for (int i = 0; i <= n; i += 1) res += max(x[i] - x[i + 1], i64(0));
    return res;
  }
};
