class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        using ll = long long;
        int n = nums.size();
        vector <int> diff(n);
        for (int i = 0; i < n; i ++) {
            diff[i] = target[i] - nums[i];
        }
        int prv = 0;
        ll ans = 0;
        for (int i = 0; i < n; i ++) {
            if (diff[i] > 0) {
                prv = max(prv, 0);
                ans += max(0, diff[i] - prv);
            }
            else if (diff[i] < 0) {
                prv = min(prv, 0);
                ans += max(0, abs(diff[i]) - abs(prv));
            }
            prv = diff[i];
        }
        return ans;
    }
};
