class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        for (int i = 0; i < nums.size(); ++i) {
            nums[i] = target[i] - nums[i];
        }
        nums.push_back(0);
        long long cnt = 0;
        cnt += minOpToKillMountain(nums);
        for (int& num: nums) {
            num = -num;
        }
        cnt += minOpToKillMountain(nums);
        return cnt;
    }
    long long minOpToKillMountain(vector<int>& nums) {
        int lower = 0;
        int prev = 0;
        int highest = 0;
        bool climbUp = true;
        long long cnt = 0;
        for (int num: nums) {
            if (num <= 0) {
                cnt += highest;
                highest = 0;
                prev = 0;
                climbUp = true;
                continue;
            }

            if (num > prev) {
                if (climbUp)
                    highest = max(num, highest);
                else {
                    cnt += (highest - lower);
                    lower = 0;
                    highest = num;
                }
                climbUp = true;
            }

            if (num < prev) {
                if (climbUp) {
                    lower = num;
                } else {
                    lower = min(lower, num);
                }
                climbUp = false;
            }
            prev = num;
        }
        return cnt;
    }
};
