class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        n=len(nums)
        if n==1:
            return abs(nums[0]-target[0])
        a=[x-y for x,y in zip(nums,target)]
        diff=[a[0]]
        for x,y in pairwise(a):
            diff.append(y-x)
        print(diff)
        pre=0    
        res=0
        for x in diff:
            new=pre+x
            if pre==0 or new*pre<0:
                res+=abs(new)
            elif abs(new)>abs(pre):
                res+=abs(x)
            pre=new
        return res
