using ll=long long;
class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        ll  res=0,op=0;
        int n=nums.size();
        vector<int>diff(n);
        for(int i=0;i<n;i++){
            diff[i]=target[i]-nums[i];
        }
        res=abs(diff[0]);
        op=diff[0];
        for(int i=1;i<n;i++){
          if(diff[i]>=0&&op>=0){
             if(diff[i]>=op){
                res+=(diff[i]-op);
             }
             op=diff[i];
          }else if(diff[i]<0&&op<0){
            if(diff[i]<=op){
                res+=(op-diff[i]);
             }
             op=diff[i];
          }else{
            res+=abs(diff[i]);
            op=diff[i];
          }
        }
        return res;
    }
};
