class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();

        long long cnt = target[0] - nums[0];
        long long ans = 0;
        for(int i=1;i<n;i++) {
            if(nums[i] <= target[i]) {
                if(cnt < 0) {
                    ans += abs(cnt);
                    cnt = target[i]-nums[i];
                }
                else {
                    if(cnt < target[i]-nums[i]) {
                        cnt = target[i]-nums[i];
                    }
                    else {
                        ans += (cnt-(target[i]-nums[i]));
                        cnt = target[i]-nums[i];
                    }
                }
            }
            else {//nums[i] > target[i];
                if(cnt > 0) {
                    ans += cnt;
                    cnt = target[i]-nums[i];
                }
                else {
                    if(abs(cnt) < abs(nums[i]-target[i])) {
                        cnt = target[i]-nums[i];
                    }
                    else {
                        ans += (abs(cnt)-abs(nums[i]-target[i]));
                        cnt = target[i]-nums[i];
                    }
                }
            }
        }
        ans += abs(cnt);

        return ans;
    }
};
