class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:

        prevNum = target[0] - nums[0]
        difList = [prevNum]
        for i in range(1, len(nums)):
            curNum = target[i] - nums[i]
            if curNum != prevNum:
                difList.append(curNum)
                prevNum = curNum


        curMax = difList[0]
        prevNum = difList[0]
        #print(difList)
        total = prevNum if prevNum > 0 else (0)
        for i in range(1, len(difList)):
            if difList[i] > prevNum:
                total += (difList[i] - prevNum)


            prevNum = difList[i]

        if prevNum < 0:
            total += abs(prevNum)

        return total
