class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n=nums.size();
        std::vector<long long> f(n+2,0);
        for(int i=1;i<=n;i++){
            int l=nums[i-1],r=target[i-1];
            if(i==1) f[i]=abs(l-r);
            else{
                int la=nums[i-2]-target[i-2];
                int nw=l-r;
                if(nw==0) f[i]=f[i-1];
                else if(la==0) f[i]=f[i-1]+abs(nw);
                else {
                    if(1ll*nw*la>0){
                        if(abs(nw)<=abs(la)) f[i]=f[i-1];
                        else{
                            f[i]=f[i-1]+abs(nw)-abs(la);
                        }
                    }else{
                        f[i]=f[i-1]+abs(nw);
                    }
                }
            }
        }
        return f[n];
    }
};
