class Solution {
public:
    long long minimumOperations(vector<int>& nums1, vector<int>& target) {
        vector<long long> nums;
        for(int i=0; i<nums1.size(); i++){
            nums.push_back(target[i] - nums1[i]);
        }
        long long i=0, ans= 0, prev=0, n=nums.size();
        while(i<n){
            ans += abs(nums[i]);
            prev = nums[i];
            i++;
            while(i<n && prev*nums[i] > 0){
                if(abs(nums[i]) > abs(prev)) ans += (abs(nums[i]) - abs(prev));
                prev = nums[i];
                i++;
            }
            //i++;
        }
        return ans;
    }
};
