class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {

        vector<int>v;

        for(int i=0;i<nums.size();i++){
            v.push_back(nums[i]-target[i]);
        }

        int g = 0;

        for(int i=0;i<v.size();i++){
            if(v[i]==0){
                g++;
            }
        }

        long long ans = 0;
        int m = 0;

        while(g<v.size()){
            int f = -1;
           // cout<<m<<endl;


            for(int i=m;i<v.size();i++){
                if(v[i]!=0){
                    f = i;
                    break;
                }
            }

            if(f==-1){
                return ans;
            }
            if(v[f]<0){
                int y = v[f];
                int u = f;
                while(u<v.size() && v[u]<0){
                    y = max(y,v[u]);
                    u++;

                };
                ans +=abs(y);
                int y1 = abs(y);
                int flag = true;
                for(int i=f;i<u;i++){

                    v[i]+=y1;
                    if(v[i]==0){
                        g++;
                        if(flag==true){
                            m = i+1;
                        }

                    }
                    else{
                        flag = false;
                    }
                }
                //m = u;
            }
            else{
                int y = v[f];
                int u = f;
                while(u<v.size() && v[u]>0){
                    y = min(y,v[u]);
                    u++;

                };
                int flag = true;
                ans +=abs(y);
                for(int i=f;i<u;i++){

                    v[i]-=y;
                    if(v[i]==0){
                        g++;
                        if(flag==true){
                            m = i+1;
                        }
                    }
                    else{
                        flag = false;
                    }
                }
               // m = u;

            }


        }

        return ans;

    }
};
