class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        N = len(nums)
        minusA = [nums[i+1] - nums[i] for i in range(0, N - 1)]
        minusA.insert(0, nums[0])
        minusB = [target[i+1] - target[i] for i in range(0, N - 1)]
        minusB.insert(0, target[0])

        a, b = 0, 0
        for x, y in zip(minusA, minusB):
            if x > y:
                a += x - y
            elif x < y:
                b += y - x
        return max(a,b)
