class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector<int> diff(n, 0);
        for (int i = 0; i != n; ++i) {
            diff[i] = nums[i] - target[i];
        }

        long long total = 0;
        for (int i = 0; i < n; ++i) {
            if (diff[i] == 0) {
                continue;
            }
            int r = i;
            if (diff[i] > 0) {
                while (r < n && diff[r] > 0) {
                    ++r;
                }
            } else {
                while (r < n && diff[r] < 0) {
                    ++r;
                }
            }

            --r;
            total += getOp(diff, i, r);
            i = r;
        }

        return total;
    }

    long long getOp(vector<int> &nums, int l, int r) {
        vector<int> ch;
        for (int i = l; i <= r; ++i) {
            if (ch.size() <= 1) {
                ch.push_back(abs(nums[i]));
                continue;
            }
            auto &pre = ch[ch.size() - 2];
            auto &cur = ch.back();
            if (pre < cur) {
                if (cur < abs(nums[i])) {
                    ch.back() = abs(nums[i]);
                } else if (cur > abs(nums[i])) {
                    ch.push_back(abs(nums[i]));
                }
            } else {
                if (cur > abs(nums[i])) {
                    ch.back() = abs(nums[i]);
                } else if (cur < abs(nums[i])) {
                    ch.push_back(abs(nums[i]));
                }
            }
        }

        int least = abs(ch[0]);
        long long cnt = 0;
        for (int i = 1; i < ch.size(); ++i) {
            int num = ch[i];
            if (num < least) {
                cnt += (long long)(least - num);
            } else {
                if (ch[i - 1] < ch[i]) {
                    cnt += (long long)(ch[i] - ch[i - 1]);
                }
            }
            least = min(num, least);
        }
        cnt += least;
        return cnt;
    }
};
