class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        vector<long long> nums2;
        nums2.push_back(0LL);
        for(int i = 0; i < nums.size(); i++)
            nums2.push_back(nums[i] - target[i]);
        nums2.push_back(0LL);
        long long ans = 0LL;
        int index = 1;
        //cout << index << '\n';

        while(index < nums2.size()){
            //cout << index << '\n';
            // if(nums[index] == 0){
            //     while(index < nums.size() && nums[index] == 0)
            //         index++;
            //     continue;
            // }
            if(nums2[index] == 0 || nums2[index-1] == 0 || ((nums2[index] > 0)&&(nums2[index-1] < 0) ) 
             || ((nums2[index] < 0) && (nums2[index-1] > 0))){
                ans += abs(nums2[index-1]);
                //cout << ans << '\n';
            }

            else{
                long long a = abs(nums2[index]);
                long long b = abs(nums2[index-1]);
                if(b-a > 0)
                    ans += b - a;

            }
            index++;

        }
        return ans;
    }
};
